mapper.insert:
INSERT INTO employee (
    emp_no,
    pw, name, dept, role, join_date
) VALUES (
    #{emp_no}, #{pw}, #{name}, #{dept}, #{role}, SYSDATE
)

mapper.update:
UPDATE employee
SET nickname = #{nickname}, ssn = #{ssn}, phone = #{phone}, email = #{email},
    zip_code = #{zip_code}, address = #{address}, detail_address = #{detail_address}
WHERE emp_no = #{emp_no}
